'    DAT4 AS (SELECT ROUND(DBMS_RANDOM.VALUE(40, 100)) AS VALUE FROM DUAL)',
'SELECT ',
'       ''icon'' AS CARD_TYPE, ',
'       DECODE(ROWNUM, 1, ''fa-globe'', 2, ''fa-gear'', ''fa-user'') AS CARD_ICON, ',
'       NULL AS CARD_ICON_COLOR, ',
'       NULL AS CARD_HEADER_STYLE,',
'       ''Material Card'' AS CARD_TITLE, ',
