'       ''https://www.valuetech.de/'' AS CARD_LINK, ',
'       ''{',
'           "labels": ["Mo", "Tue", "Wed", "Th", "Fr"],',
'            "series": [['' ||(SELECT * FROM   DAT1) ||''], ',
'                       ['' ||(SELECT * FROM   DAT2) ||''],',
'                       ['' ||(SELECT * FROM   DAT3) ||'']]',
'        }'' AS CARD_CHART_DATA,',
